class Turbu_cal(object):
    
    def __init__(self,veloctiy,diameter,kviscosity,density):
        
        self.velocity = float(veloctiy)
        self.hyd_dia = float(diameter)
        self.nu = float(kviscosity)
        self.rho = float(density)
        
        self.Re = self.rho*self.velocity*self.hyd_dia/self.nu
        
    def ke_cal(self):
        
        cmu = 0.09
        self.tur_den = 0.16*self.Re**(-0.128)
        self.tur_energe = 1.5*(self.velocity*self.tur_den)**2
        self.tur_lenscal = 0.038*self.hyd_dia
        self.tur_dis_e = cmu**0.75*self.tur_energe**1.5/self.tur_lenscal
        self.tur_dis_w = cmu**(-0.25)*self.tur_energe**0.5/self.tur_lenscal
        
        return [float('%.4f' % self.tur_energe),float('%.4f' % self.tur_dis_e),float('%.4f' % self.tur_dis_w)]
        """
        print("Re: ",self.Re)
        print("k: ",self.tur_energe)
        print("e: ",self.tur_dis_e)
        print("w: ",self.tur_dis_w)
        """
if __name__ == '__main__':
    
    turbu_cal = Turbu_cal(15,4.5,1.8e-5,1.0)
    
    turbu_cal.ke_cal()
    
    
    
   
        
        
        
        
        
        
        